import { InternalOptions } from "../../lib/types";
import { IncomingRequest, OutgoingResponse } from "..";
import { Cookie } from "../lib/cookie";
/** Takes a request and response, and gives renderable pages */
export default function renderPage({ options, query, cookies, }: {
    options: InternalOptions;
    query: IncomingRequest["query"];
    cookies: Cookie[];
}): {
    signin(props?: any): OutgoingResponse<any>;
    signout(props?: any): OutgoingResponse<any>;
    verifyRequest(props?: any): OutgoingResponse<any>;
    error(props: any): OutgoingResponse<any>;
};
